import 'package:flutter/material.dart';\n\nclass PayScreen extends StatelessWidget {\n  const PayScreen({super.key});\n\n  @override\n  Widget build(BuildContext context) {\n    return Scaffold(\n      backgroundColor: Theme.of(context).colorScheme.surface,\n      appBar: AppBar(\n        title: const Text(\n          'Nexora Pay',\n          style: TextStyle(fontWeight: FontWeight.bold),\n        ),\n        actions: [\n          IconButton(\n            icon: const Icon(Icons.notifications_outlined),\n            onPressed: () {},\n          ),\n          IconButton(\n            icon: const Icon(Icons.history),\n            onPressed: () {},\n          ),\n        ],\n      ),\n      body: SingleChildScrollView(\n        child: Column(\n          children: [\n            // Wallet Balance Card\n            Container(\n              margin: const EdgeInsets.all(16),\n              padding: const EdgeInsets.all(24),\n              decoration: BoxDecoration(\n                gradient: LinearGradient(\n                  colors: [\n                    Theme.of(context).colorScheme.primary,\n                    Theme.of(context).colorScheme.tertiary,\n                  ],\n                ),\n                borderRadius: BorderRadius.circular(20),\n                boxShadow: [\n                  BoxShadow(\n                    color: Theme.of(context).colorScheme.primary.withOpacity(0.3),\n                    blurRadius: 15,\n                    offset: const Offset(0, 8),\n                  ),\n                ],\n              ),\n              child: Column(\n                crossAxisAlignment: CrossAxisAlignment.start,\n                children: [\n                  Row(\n                    mainAxisAlignment: MainAxisAlignment.spaceBetween,\n                    children: [\n                      const Text(\n                        'Total Balance',\n                        style: TextStyle(\n                          color: Colors.white70,\n                          fontSize: 14,\n                        ),\n                      ),\n                      Container(\n                        padding: const EdgeInsets.symmetric(\n                          horizontal: 12,\n                          vertical: 4,\n                        ),\n                        decoration: BoxDecoration(\n                          color: Colors.white24,\n                          borderRadius: BorderRadius.circular(12),\n                        ),\n                        child: const Row(\n                          children: [\n                            Icon(Icons.shield, color: Colors.white, size: 14),\n                            SizedBox(width: 4),\n                            Text(\n                              'Verified',\n                              style: TextStyle(\n                                color: Colors.white,\n                                fontSize: 11,\n                                fontWeight: FontWeight.bold,\n                              ),\n                            ),\n                          ],\n                        ),\n                      ),\n                    ],\n                  ),\n                  const SizedBox(height: 8),\n                  const Text(\n                    '\$2,459.80',\n                    style: TextStyle(\n                      color: Colors.white,\n                      fontSize: 36,\n                      fontWeight: FontWeight.bold,\n                    ),\n                  ),\n                  const SizedBox(height: 4),\n                  const Text(\n                    '+ \$120.50 this week',\n                    style: TextStyle(\n                      color: Colors.greenAccent,\n                      fontSize: 13,\n                      fontWeight: FontWeight.w600,\n                    ),\n                  ),\n                ],\n              ),\n            ),\n\n            // Quick Actions\n            Padding(\n              padding: const EdgeInsets.symmetric(horizontal: 16),\n              child: Row(\n                mainAxisAlignment: MainAxisAlignment.spaceAround,\n                children: [\n                  _buildQuickAction(\n                    context,\n                    Icons.arrow_upward,\n                    'Send',\n                    Colors.blue,\n                  ),\n                  _buildQuickAction(\n                    context,\n                    Icons.arrow_downward,\n                    'Receive',\n                    Colors.green,\n                  ),\n                  _buildQuickAction(\n                    context,\n                    Icons.swap_horiz,\n                    'Exchange',\n                    Colors.orange,\n                  ),\n                  _buildQuickAction(\n                    context,\n                    Icons.account_balance,\n                    'Withdraw',\n                    Colors.purple,\n                  ),\n                ],\n              ),\n            ),\n\n            const SizedBox(height: 24),\n\n            // Payment Methods\n            Padding(\n              padding: const EdgeInsets.symmetric(horizontal: 16),\n              child: Column(\n                crossAxisAlignment: CrossAxisAlignment.start,\n                children: [\n                  const Text(\n                    'Payment Methods',\n                    style: TextStyle(\n                      fontSize: 18,\n                      fontWeight: FontWeight.bold,\n                    ),\n                  ),\n                  const SizedBox(height: 12),\n                  _buildPaymentMethod(\n                    context,\n                    'M-Pesa',\n                    '+254 712 345 678',\n                    Icons.phone_android,\n                    Colors.green,\n                  ),\n                  _buildPaymentMethod(\n                    context,\n                    'PayPal',\n                    'user@email.com',\n                    Icons.payment,\n                    Colors.blue,\n                  ),\n                  _buildPaymentMethod(\n                    context,\n                    'Visa',\n                    '**** **** **** 4532',\n                    Icons.credit_card,\n                    Colors.purple,\n                  ),\n                  _buildPaymentMethod(\n                    context,\n                    'Crypto Wallet',\n                    'BTC, ETH, USDT',\n                    Icons.currency_bitcoin,\n                    Colors.orange,\n                  ),\n                ],\n              ),\n            ),\n\n            const SizedBox(height: 24),\n\n            // Recent Transactions\n            Padding(\n              padding: const EdgeInsets.symmetric(horizontal: 16),\n              child: Column(\n                crossAxisAlignment: CrossAxisAlignment.start,\n                children: [\n                  Row(\n                    mainAxisAlignment: MainAxisAlignment.spaceBetween,\n                    children: [\n                      const Text(\n                        'Recent Transactions',\n                        style: TextStyle(\n                          fontSize: 18,\n                          fontWeight: FontWeight.bold,\n                        ),\n                      ),\n                      TextButton(\n                        onPressed: () {},\n                        child: const Text('View All'),\n                      ),\n                    ],\n                  ),\n                  const SizedBox(height: 8),\n                  _buildTransaction(\n                    context,\n                    'Payment Received',\n                    'From John Doe',\n                    '+ \$150.00',\n                    Colors.green,\n                    Icons.arrow_downward,\n                  ),\n                  _buildTransaction(\n                    context,\n                    'Sent to Sarah',\n                    'For freelance work',\n                    '- \$80.00',\n                    Colors.red,\n                    Icons.arrow_upward,\n                  ),\n                  _buildTransaction(\n                    context,\n                    'Escrow Payment',\n                    'Project milestone',\n                    '- \$200.00',\n                    Colors.orange,\n                    Icons.lock,\n                  ),\n                ],\n              ),\n            ),\n\n            const SizedBox(height: 80),\n          ],\n        ),\n      ),\n    );\n  }\n\n  Widget _buildQuickAction(\n    BuildContext context,\n    IconData icon,\n    String label,\n    Color color,\n  ) {\n    return Column(\n      children: [\n        Container(\n          width: 60,\n          height: 60,\n          decoration: BoxDecoration(\n            color: color.withOpacity(0.1),\n            borderRadius: BorderRadius.circular(16),\n          ),\n          child: Icon(icon, color: color, size: 28),\n        ),\n        const SizedBox(height: 8),\n        Text(\n          label,\n          style: TextStyle(\n            fontSize: 12,\n            fontWeight: FontWeight.w600,\n            color: Theme.of(context).colorScheme.onSurface,\n          ),\n        ),\n      ],\n    );\n  }\n\n  Widget _buildPaymentMethod(\n    BuildContext context,\n    String name,\n    String detail,\n    IconData icon,\n    Color color,\n  ) {\n    return Container(\n      margin: const EdgeInsets.only(bottom: 12),\n      padding: const EdgeInsets.all(16),\n      decoration: BoxDecoration(\n        color: Theme.of(context).colorScheme.surfaceContainerHighest,\n        borderRadius: BorderRadius.circular(12),\n      ),\n      child: Row(\n        children: [\n          Container(\n            padding: const EdgeInsets.all(12),\n            decoration: BoxDecoration(\n              color: color.withOpacity(0.1),\n              borderRadius: BorderRadius.circular(10),\n            ),\n            child: Icon(icon, color: color, size: 24),\n          ),\n          const SizedBox(width: 16),\n          Expanded(\n            child: Column(\n              crossAxisAlignment: CrossAxisAlignment.start,\n              children: [\n                Text(\n                  name,\n                  style: const TextStyle(\n                    fontWeight: FontWeight.bold,\n                    fontSize: 15,\n                  ),\n                ),\n                const SizedBox(height: 2),\n                Text(\n                  detail,\n                  style: TextStyle(\n                    color: Theme.of(context).colorScheme.outline,\n                    fontSize: 12,\n                  ),\n                ),\n              ],\n            ),\n          ),\n          Icon(\n            Icons.arrow_forward_ios,\n            size: 16,\n            color: Theme.of(context).colorScheme.outline,\n          ),\n        ],\n      ),\n    );\n  }\n\n  Widget _buildTransaction(\n    BuildContext context,\n    String title,\n    String subtitle,\n    String amount,\n    Color amountColor,\n    IconData icon,\n  ) {\n    return Container(\n      margin: const EdgeInsets.only(bottom: 12),\n      padding: const EdgeInsets.all(16),\n      decoration: BoxDecoration(\n        color: Theme.of(context).colorScheme.surfaceContainerHighest,\n        borderRadius: BorderRadius.circular(12),\n      ),\n      child: Row(\n        children: [\n          Container(\n            padding: const EdgeInsets.all(10),\n            decoration: BoxDecoration(\n              color: amountColor.withOpacity(0.1),\n              shape: BoxShape.circle,\n            ),\n            child: Icon(icon, color: amountColor, size: 20),\n          ),\n          const SizedBox(width: 16),\n          Expanded(\n            child: Column(\n              crossAxisAlignment: CrossAxisAlignment.start,\n              children: [\n                Text(\n                  title,\n                  style: const TextStyle(\n                    fontWeight: FontWeight.bold,\n                    fontSize: 14,\n                  ),\n                ),\n                const SizedBox(height: 2),\n                Text(\n                  subtitle,\n                  style: TextStyle(\n                    color: Theme.of(context).colorScheme.outline,\n                    fontSize: 12,\n                  ),\n                ),\n              ],\n            ),\n          ),\n          Text(\n            amount,\n            style: TextStyle(\n              color: amountColor,\n              fontWeight: FontWeight.bold,\n              fontSize: 15,\n            ),\n          ),\n        ],\n      ),\n    );\n  }\n}