import 'package:flutter/material.dart';\n\nclass VideoScreen extends StatelessWidget {\n  const VideoScreen({super.key});\n\n  @override\n  Widget build(BuildContext context) {\n    return Scaffold(\n      backgroundColor: Colors.black,\n      body: Stack(\n        children: [\n          // Video Feed (TikTok-style)\n          PageView.builder(\n            scrollDirection: Axis.vertical,\n            itemCount: 10,\n            itemBuilder: (context, index) {\n              return Stack(\n                fit: StackFit.expand,\n                children: [\n                  // Video Placeholder\n                  Container(\n                    decoration: BoxDecoration(\n                      gradient: LinearGradient(\n                        begin: Alignment.topLeft,\n                        end: Alignment.bottomRight,\n                        colors: [\n                          Colors.purple.shade900,\n                          Colors.blue.shade900,\n                        ],\n                      ),\n                    ),\n                    child: Center(\n                      child: Column(\n                        mainAxisAlignment: MainAxisAlignment.center,\n                        children: [\n                          Icon(\n                            Icons.play_circle_outline,\n                            size: 80,\n                            color: Colors.white.withOpacity(0.7),\n                          ),\n                          const SizedBox(height: 16),\n                          Text(\n                            'Video ${index + 1}',\n                            style: TextStyle(\n                              color: Colors.white.withOpacity(0.7),\n                              fontSize: 18,\n                            ),\n                          ),\n                        ],\n                      ),\n                    ),\n                  ),\n\n                  // Bottom Gradient\n                  Positioned(\n                    bottom: 0,\n                    left: 0,\n                    right: 0,\n                    child: Container(\n                      height: 200,\n                      decoration: BoxDecoration(\n                        gradient: LinearGradient(\n                          begin: Alignment.topCenter,\n                          end: Alignment.bottomCenter,\n                          colors: [\n                            Colors.transparent,\n                            Colors.black.withOpacity(0.8),\n                          ],\n                        ),\n                      ),\n                    ),\n                  ),\n\n                  // Video Info\n                  Positioned(\n                    bottom: 80,\n                    left: 16,\n                    right: 80,\n                    child: Column(\n                      crossAxisAlignment: CrossAxisAlignment.start,\n                      children: [\n                        Row(\n                          children: [\n                            const CircleAvatar(\n                              radius: 18,\n                              backgroundColor: Colors.white,\n                              child: Icon(Icons.person, size: 20),\n                            ),\n                            const SizedBox(width: 10),\n                            const Text(\n                              '@creator${index + 1}',\n                              style: TextStyle(\n                                color: Colors.white,\n                                fontWeight: FontWeight.bold,\n                                fontSize: 15,\n                              ),\n                            ),\n                            const SizedBox(width: 10),\n                            Container(\n                              padding: const EdgeInsets.symmetric(\n                                horizontal: 12,\n                                vertical: 4,\n                              ),\n                              decoration: BoxDecoration(\n                                border: Border.all(color: Colors.white),\n                                borderRadius: BorderRadius.circular(4),\n                              ),\n                              child: const Text(\n                                'Follow',\n                                style: TextStyle(\n                                  color: Colors.white,\n                                  fontSize: 12,\n                                  fontWeight: FontWeight.bold,\n                                ),\n                              ),\n                            ),\n                          ],\n                        ),\n                        const SizedBox(height: 12),\n                        const Text(\n                          'Amazing content about technology and innovation! ðŸš€',\n                          style: TextStyle(\n                            color: Colors.white,\n                            fontSize: 14,\n                          ),\n                          maxLines: 2,\n                          overflow: TextOverflow.ellipsis,\n                        ),\n                        const SizedBox(height: 8),\n                        Row(\n                          children: [\n                            const Icon(\n                              Icons.music_note,\n                              color: Colors.white,\n                              size: 14,\n                            ),\n                            const SizedBox(width: 6),\n                            Expanded(\n                              child: Text(\n                                'Original Sound - Creator ${index + 1}',\n                                style: const TextStyle(\n                                  color: Colors.white,\n                                  fontSize: 12,\n                                ),\n                                maxLines: 1,\n                                overflow: TextOverflow.ellipsis,\n                              ),\n                            ),\n                          ],\n                        ),\n                      ],\n                    ),\n                  ),\n\n                  // Right Side Actions\n                  Positioned(\n                    right: 8,\n                    bottom: 80,\n                    child: Column(\n                      children: [\n                        _buildActionButton(\n                          Icons.favorite_border,\n                          '${(index + 1) * 1234}',\n                        ),\n                        const SizedBox(height: 24),\n                        _buildActionButton(\n                          Icons.comment,\n                          '${(index + 1) * 89}',\n                        ),\n                        const SizedBox(height: 24),\n                        _buildActionButton(\n                          Icons.share,\n                          '${(index + 1) * 45}',\n                        ),\n                        const SizedBox(height: 24),\n                        _buildActionButton(\n                          Icons.monetization_on,\n                          '+${(index + 1) * 5}',\n                        ),\n                      ],\n                    ),\n                  ),\n                ],\n              );\n            },\n          ),\n\n          // Top Bar\n          Positioned(\n            top: 0,\n            left: 0,\n            right: 0,\n            child: SafeArea(\n              child: Container(\n                padding: const EdgeInsets.symmetric(horizontal: 16, vertical: 12),\n                child: Row(\n                  mainAxisAlignment: MainAxisAlignment.spaceBetween,\n                  children: [\n                    const Text(\n                      'Nexora Video',\n                      style: TextStyle(\n                        color: Colors.white,\n                        fontSize: 18,\n                        fontWeight: FontWeight.bold,\n                      ),\n                    ),\n                    Row(\n                      children: [\n                        IconButton(\n                          icon: const Icon(Icons.search, color: Colors.white),\n                          onPressed: () {},\n                        ),\n                        IconButton(\n                          icon: const Icon(Icons.video_call, color: Colors.white),\n                          onPressed: () {},\n                        ),\n                      ],\n                    ),\n                  ],\n                ),\n              ),\n            ),\n          ),\n\n          // Category Tabs\n          Positioned(\n            top: 60,\n            left: 0,\n            right: 0,\n            child: SafeArea(\n              child: Container(\n                height: 40,\n                child: ListView(\n                  scrollDirection: Axis.horizontal,\n                  padding: const EdgeInsets.symmetric(horizontal: 16),\n                  children: [\n                    _buildCategoryChip('For You', true),\n                    _buildCategoryChip('Following', false),\n                    _buildCategoryChip('Technology', false),\n                    _buildCategoryChip('Education', false),\n                    _buildCategoryChip('Entertainment', false),\n                  ],\n                ),\n              ),\n            ),\n          ),\n        ],\n      ),\n    );\n  }\n\n  Widget _buildActionButton(IconData icon, String label) {\n    return Column(\n      children: [\n        Container(\n          padding: const EdgeInsets.all(10),\n          decoration: BoxDecoration(\n            color: Colors.white.withOpacity(0.2),\n            shape: BoxShape.circle,\n          ),\n          child: Icon(\n            icon,\n            color: Colors.white,\n            size: 28,\n          ),\n        ),\n        const SizedBox(height: 4),\n        Text(\n          label,\n          style: const TextStyle(\n            color: Colors.white,\n            fontSize: 11,\n            fontWeight: FontWeight.w600,\n          ),\n        ),\n      ],\n    );\n  }\n\n  Widget _buildCategoryChip(String label, bool isSelected) {\n    return Container(\n      margin: const EdgeInsets.only(right: 8),\n      padding: const EdgeInsets.symmetric(horizontal: 16, vertical: 8),\n      decoration: BoxDecoration(\n        color: isSelected\n            ? Colors.white\n            : Colors.white.withOpacity(0.2),\n        borderRadius: BorderRadius.circular(20),\n      ),\n      child: Text(\n        label,\n        style: TextStyle(\n          color: isSelected ? Colors.black : Colors.white,\n          fontWeight: FontWeight.bold,\n          fontSize: 13,\n        ),\n      ),\n    );\n  }\n}